    <h1>测试</h1>
    <router-view/>
  </div>
</template>

<script>
export default {
  methods: {
    addInterval () {
      const timer = setInterval(() => {
        console.log('aaa')
      }, 100)
      this.$once('hook:beforeDestroy', () => {
        clearInterval(timer)
      })
    }
  }
}
</script>
```

